def parse_basic_greek():
    filename = 'basic_greek_mapping.csv'
    f = open(filename, 'r')
    mapping = {}
    tuples = []
    # eat the first line
    f.readline()
    for line in f:
        line = line.strip().decode('utf8')
        pair = line.split(',')
        mapping[pair[0]] = pair[1]
        tuples.append((pair[0],pair[1]))
    f.close()
    return mapping

