def parse_extended_greek_mapping():
    import codecs
    filename = 'extended_greek_mapping.csv'
    f = codecs.open(filename, 'r', 'utf-8')
    tuples = []
    mapping = {}
    for line in f:
        line = line.strip()
        pair = line.split(',')
        mapping[pair[0]] = pair[1]
        tuples.append((pair[0],pair[1]))
    f.close()
    return (tuples, mapping)

